<script>
    var nextWeekButton = document.getElementById('next-week-button');
    var prevWeekButton = document.getElementById('prev-week-button');
    var dateRangeDiv = document.getElementById('date-range');
    var date = new Date();
    var currentDate = new Date(date).setDate(date.getDate() - (date.getDay() % 7) + 1);
    var clickingNum = 0;
    var clickingPrev = 0;
    var weekDaysDiv = document.querySelector('.week-days');

    nextWeekButton.addEventListener('click', function() {
        // Utwórz nową datę na podstawie aktualnej daty
        var newDate = new Date(currentDate);
        clickingNum++;
        clickingPrev = 0
        newDate.setDate(newDate.getDate() + 7);

        // Utwórz nową datę na koniec tygodnia
        var endOfWeek = new Date(newDate);
        endOfWeek.setDate(newDate.getDate() + 6);

        // Formatuj daty w formacie "DZIEN_MIESIAC - DZIEN_MIESIAC"
        var formattedStartDate = formatDate(newDate);
        var formattedEndDate = formatDate(endOfWeek);

        // Ustaw nową wartość w elemencie .date-range
        dateRangeDiv.textContent = formattedStartDate + ' - ' + formattedEndDate;
        currentDate = newDate
        updateWeekDays(newDate)
    });
    prevWeekButton.addEventListener('click', function() {
        var newDate = new Date(currentDate);
        clickingPrev++;
        clickingNum = 0;
        newDate.setDate(newDate.getDate() - 7);
        // Utwórz nową datę na koniec tygodnia
        var endOfWeek = new Date(newDate);
        endOfWeek.setDate(newDate.getDate() + 6);

        // Formatuj daty w formacie "DZIEN_MIESIAC - DZIEN_MIESIAC"
        var formattedStartDate = formatDate(newDate);
        var formattedEndDate = formatDate(endOfWeek);

        // Ustaw nową wartość w elemencie .date-range
        dateRangeDiv.textContent = formattedStartDate + ' - ' + formattedEndDate;
        updateWeekDays(newDate)
        currentDate = newDate
    });
    // Funkcja do formatowania daty w odpowiednim formacie
    function formatDate(date) {
        var day = date.getDate();
        var month = date.toLocaleString('default', { month: 'long' });

        return day + '. ' + month;
    }

    function updateWeekDays(startDate) {
        var daysOfWeek = ['Pn', 'Wt', 'Śr', 'Cz', 'Pt', 'So', 'Nd'];
        var dayNumberElements = document.querySelectorAll('.day-number');

        for (var i = 0; i < dayNumberElements.length; i++) {
            var dayNumber = startDate.getDate() + i;
            dayNumberElements[i].textContent = dayNumber;
           /* if (dayNumber > 31) {
                dayNumber -= 31;
            }*/
        }
    }

    function selectDay(button) {
        // Usuń klasę selected ze wszystkich przycisków
        var allButtons = document.querySelectorAll('.week-days .day');
        allButtons.forEach(function(btn) {
            btn.classList.remove('selected');
        });

        // Dodaj klasę selected do klikniętego przycisku
        button.classList.add('selected');
    }
</script>

